import 'package:flutter/material.dart';import '../../constants/colors_const.dart';import '../../utils/app_utils.dart';import '../big_text_widget.dart';//ignore: must_be_immutableclass CustomButton extends StatelessWidget {  final bool isLoading;  final void Function() onPressed;  final String title;  final Widget? icon;  Color? colors;  double? width;  double? height;  double? radius;  CustomButton(      {super.key,      required this.isLoading,      required this.onPressed,      this.width,      this.height,      this.radius = 8.0,      this.colors = ColorConst.dashboardCardColorRozviy,      required this.title,      this.icon});  @override  Widget build(BuildContext context) {    return ElevatedButton(      style: ElevatedButton.styleFrom(        foregroundColor: Colors.white,        backgroundColor: colors,        shape: RoundedRectangleBorder(          borderRadius: BorderRadius.circular(radius ?? 8.0),        ),      ),      onPressed: isLoading ? null : onPressed,      child: SizedBox(        width: width,        height: height,        child: Center(          child: isLoading == false              ? Row(            mainAxisAlignment: MainAxisAlignment.center,                  children: [                    Row(children: [                      icon ?? const SizedBox(),                      const SizedBox(width: 30),                      BigText(                          text: title,                          fontWidget: FontWeight.w700,                          color: ColorConst.whiteColor,                          size: 30),                      const SizedBox(width: 60),                    ],)                  ],                )              : AppUtils.buttonLoader,        ),      ),    );  }}