import 'package:flutter/material.dart';class MyInheritedWidget extends InheritedWidget {  const MyInheritedWidget({super.key, required super.child, required this.onTap,});  final void Function() onTap;  static MyInheritedWidget? of(BuildContext context) {    return context.dependOnInheritedWidgetOfExactType<MyInheritedWidget>();  }  @override  bool updateShouldNotify(MyInheritedWidget oldWidget) {    return  oldWidget != this;  }}